package main.java;// SortDemo.java     import java.util.*;   // for class StringTokenizerpublic class SortDemoData  {                       private Item[] _myArray;        private String _algo;        public SortDemoData(){        	_myArray = null;        }                public Item[] getArray() {        	return _myArray;        }                public String getAlgo() {        	return _algo;        }                // read numbers from input to array myArray        public void initializeArray(String string) throws NumberFormatException,         NoSuchElementException, NegativeArraySizeException{	        	StringTokenizer inputTokens = new StringTokenizer(string);	        	String[] result = string.split("\\s");                String str = "";                                    int n; // will be the length of myArray                        if (inputTokens.hasMoreTokens()) {                        n = inputTokens.countTokens();                 } else {                        throw new NoSuchElementException();                }                // define the array of the right length n                    if (n == 1) {                         // interpret the next number as number of entries and                         // generate a random vector with entries 1 ... n                        str = inputTokens.nextToken();                         n =  Integer.valueOf(str).intValue();                         // may throw NegativeArraySizeException                        _myArray = new Item[n];                        boolean[] tempArray = new boolean[n + 1];                        RandomNumber randGen = new RandomNumber();                        int randNr;                        for (int i = 0; i < n; i++) {                                do                                         randNr = randGen.nextIntRand(1, n);                                while (tempArray[randNr]);                                                  tempArray[randNr] = true;                                _myArray[i] = new Item(randNr);                        }                } else {                                   Item [] array = new Item[n];                        int count =0;                        // read the n numbers into the array                        for (int i = 0; i < n; i++) {                        	                                str = result[i];                                if(Arrays.asList(Arrays.copyOfRange(result, 0, i)).contains(str)){                                	count = count + 1;                                }                                else{                                	array[i-count] = new Item(                                 	        Integer.valueOf(str).intValue());                                }                                                        }                        _myArray = new Item[n-count];                        _myArray = Arrays.copyOfRange(array, 0, n-count);                                                               }         }                     /**    	 * All methods should have a Javadoc according to STYLE.    	 * @param choice: chosen sort algorithm, needs to be a number between 0 and 5. If the number is out of range 0 should be chosen    	 * The attribute algo should always contain the choosen algorithm as string    	 * @throws Exception as per typical main specifications    	 */        public StringBuffer runAlgo(int choice) {                             Item[] copyOfMyArray = new Item[_myArray.length];                        System.arraycopy(                                _myArray, 0, copyOfMyArray, 0, _myArray.length);                                                //SER 316                        //if (choice < 0 || choice > 5) choice = 0;                                                if(choice < 1 || choice > 5) {	//choice == 0 or default                        	SortAlgos.bubbleSort(_myArray);                  			_algo = "Bubble Sort";                        }                        else if(choice == 1) {                        	SortAlgos.selectionSort(_myArray);                 			_algo = "Selection Sort";                        }                        else if(choice == 2) {                        	SortAlgos.insertionSort(_myArray);                 			_algo = "Insertion Sort";                        }                        else if(choice == 3) {                        	SortAlgos.mergeSort(_myArray);                			_algo = "Merge Sort";                        }                        else if(choice == 4) {                        	SortAlgos.quickSort(_myArray);                 			_algo = "Quick Sort";                        }                        else if(choice == 5) {                        	SortAlgos.heapSort(_myArray);                     			_algo = "Heap Sort";                        }                                                // output initial sequence of unsorted and sorted array                         int limit = Math.min(_myArray.length, 1024);                        StringBuffer outputBuf = new StringBuffer();                        outputBuf.append(_algo + "\n");                        for (int i = 0; i < limit; i++) {                                outputBuf.append(                                        i + ": "                                           + Integer.toString(                                                        copyOfMyArray[i].key)                                            + " --> "                                          + Integer.toString(_myArray[i].key)                                           + "\n");                        }                        outputBuf.append("\r");                        		               return outputBuf;                    }        		}